    func getStatus(points: Int) -> Status {
        let statuses = getStatuses()
        return statuses.filter { (status) -> Bool in
            status.points <= points
        }.sort { (status1, status2) -> Bool in
            status1.points < status2.points
        }.last!
    }
    
